import { useState } from "react";
import "./App.css";


type Note = {
  id: number;
  title:string;
  content:string
}

const App = () => {
  const [notes, setNotes] = useState<Note[]>([
    {
      id:1,
      title:'note title 1',
      content: 'note one is here'
    },
    {
      id:2,
      title:'note title 2',
      content: 'note one is here'
    },
    {
      id:3,
      title:'note title 3',
      content: 'note one is here'
    },
    {
      id:4,
      title:'note title 4',
      content: 'note one is here'
    },
  ]);

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
  }
  return (
    <div className="app-container">
      <form className="note-form">
        <input placeholder="title"
        value={title}
        onChange={(e)=>setTitle(e.target.value)} required></input>
        <textarea placeholder="Content" value={content}
        onChange={(e)=>setContent(e.target.value)} rows={10} required></textarea>
        <button type="submit" className="">
          Add Note
        </button>
      </form>
      <div className="notes-grid">
        {notes.map((note)=>(
          <div className="note-item">
          <div className="notes-header">
            <button>X</button>
          </div>
          <h2>{note.title}</h2>
          <p>{note.content}</p>
        </div>
        ))}

      </div>
    </div>
  );
};

export default App;
